mapper.select:
select 

from latestsalary
where latestid = #{latestid,jdbcType=INTEGER}
  and sid = #{sid,jdbcType=INTEGER}

mapper.delete:
delete from latestsalary
where latestid = #{latestid,jdbcType=INTEGER}
  and sid = #{sid,jdbcType=INTEGER}

mapper.insert:
insert into latestsalary (latestid, sid, companyname, 
  jobname, salary, createtime
  )
values (#{latestid,jdbcType=INTEGER}, #{sid,jdbcType=INTEGER}, #{companyname,jdbcType=VARCHAR}, 
  #{jobname,jdbcType=VARCHAR}, #{salary,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}
  )

mapper.update:
update latestsalary
set companyname = #{companyname,jdbcType=VARCHAR},
  jobname = #{jobname,jdbcType=VARCHAR},
  salary = #{salary,jdbcType=VARCHAR},
  createtime = #{createtime,jdbcType=TIMESTAMP}
where latestid = #{latestid,jdbcType=INTEGER}
  and sid = #{sid,jdbcType=INTEGER}
