mapper.select:
select 

from search
where searchid = #{searchid,jdbcType=INTEGER}
  and cid = #{cid,jdbcType=INTEGER}

mapper.delete:
delete from search
where searchid = #{searchid,jdbcType=INTEGER}
  and cid = #{cid,jdbcType=INTEGER}

mapper.insert:
insert into search (searchid, cid, companyname
  )
values (#{searchid,jdbcType=INTEGER}, #{cid,jdbcType=INTEGER}, #{companyname,jdbcType=VARCHAR}
  )

mapper.update:
update search


    companyname = #{companyname,jdbcType=VARCHAR},


where searchid = #{searchid,jdbcType=INTEGER}
  and cid = #{cid,jdbcType=INTEGER}

update search
set companyname = #{companyname,jdbcType=VARCHAR}
where searchid = #{searchid,jdbcType=INTEGER}
  and cid = #{cid,jdbcType=INTEGER}
