mapper.select:
select 

,

from users
where uid = #{uid,jdbcType=INTEGER}
  and openid = #{openid,jdbcType=VARCHAR}

mapper.delete:
delete from users
where uid = #{uid,jdbcType=INTEGER}
  and openid = #{openid,jdbcType=VARCHAR}

mapper.update:
update users


    marklist = #{marklist,jdbcType=LONGVARCHAR},


    commentids = #{commentids,jdbcType=LONGVARCHAR},


where uid = #{uid,jdbcType=INTEGER}
  and openid = #{openid,jdbcType=VARCHAR}

update users
set marklist = #{marklist,jdbcType=LONGVARCHAR},
  commentids = #{commentids,jdbcType=LONGVARCHAR}
where uid = #{uid,jdbcType=INTEGER}
  and openid = #{openid,jdbcType=VARCHAR}
